import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import LatexEditor from "./components/LatexEditor";
import NotesList from "./components/NotesList";

const API = "http://localhost:5000/api/notes";

export default function App() {
  const [notes, setNotes] = useState([]);
  const [selected, setSelected] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [unsaved, setUnsaved] = useState(false);
  const saveTimeout = useRef(null);

  // Fetch notes
  useEffect(() => {
    axios.get(API).then(res => setNotes(res.data));
  }, []);

  // Select note
  const handleSelect = note => {
    setSelected(note);
    setTitle(note.title);
    setContent(note.content);
    setUnsaved(false);
    if (saveTimeout.current) clearTimeout(saveTimeout.current);
  };

  // Auto-save with debounce
  useEffect(() => {
    if (!selected) return;
    setUnsaved(true);
    if (saveTimeout.current) clearTimeout(saveTimeout.current);
    saveTimeout.current = setTimeout(() => {
      handleSave();
    }, 1200); // 1.2s debounce
    // eslint-disable-next-line
  }, [title, content]);

  // Save note (create or update)
  const handleSave = async () => {
    if (!title || !content) return;
    if (selected) {
      // Update
      const res = await axios.put(`${API}/${selected._id}`, {
        title,
        content
      });
      setNotes(notes =>
        notes.map(n => (n._id === res.data._id ? res.data : n))
      );
      setSelected(res.data);
    } else {
      // Create
      const res = await axios.post(API, { title, content });
      setNotes([res.data, ...notes]);
      setSelected(res.data);
    }
    setUnsaved(false);
  };

  // Delete note
  const handleDelete = async id => {
    await axios.delete(`${API}/${id}`);
    setNotes(notes => notes.filter(n => n._id !== id));
    if (selected && selected._id === id) {
      setSelected(null);
      setTitle("");
      setContent("");
      setUnsaved(false);
    }
  };

  // New note
  const handleNew = () => {
    setSelected(null);
    setTitle("");
    setContent("");
    setUnsaved(false);
    if (saveTimeout.current) clearTimeout(saveTimeout.current);
  };

  return (
    <div className="flex h-screen">
      <NotesList
        notes={notes}
        onSelect={handleSelect}
        onDelete={handleDelete}
        selectedId={selected?._id}
      />
      <div className="flex-1 p-4">
        <div className="flex items-center gap-2 mb-4">
          <input
            className="border p-2 flex-1"
            placeholder="Title"
            value={title}
            onChange={e => setTitle(e.target.value)}
            aria-label="Note title"
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={handleSave}
            disabled={!title || !content || !unsaved}
            aria-label={selected ? "Update note" : "Create note"}
          >
            {selected ? "Update" : "Create"}
          </button>
          <button
            className="bg-gray-300 px-4 py-2 rounded"
            onClick={handleNew}
            aria-label="New note"
          >
            New
          </button>
          {unsaved && (
            <span className="ml-2 text-yellow-600 animate-pulse" aria-live="polite">Unsaved changes...</span>
          )}
        </div>
        <LatexEditor value={content} setValue={setContent} />
      </div>
    </div>
  );
}
