import React from "react";

const symbols = [
  { label: "∑", latex: "\\sum" },
  { label: "∫", latex: "\\int" },
  { label: "√", latex: "\\sqrt{}" },
  { label: "π", latex: "\\pi" },
  { label: "lim", latex: "\\lim_{x \\to 0}" },
  { label: "→", latex: "\\to" },
  { label: "⇌", latex: "\\rightleftharpoons" },
  { label: "H₂O", latex: "H_2O" },
  { label: "CO₂", latex: "CO_2" },
  { label: "Δ", latex: "\\Delta" },
];

export default function Toolbar({ onInsert }) {
  return (
    <div className="flex flex-wrap gap-2 mb-2">
      {symbols.map(sym => (
        <button
          key={sym.label}
          type="button"
          onClick={() => onInsert(sym.latex)}
          className="border px-2 py-1 rounded bg-gray-100 hover:bg-gray-200"
        >
          {sym.label}
        </button>
      ))}
    </div>
  );
}
