import React, { useState } from "react";

export default function NotesList({ notes, onSelect, onDelete, selectedId }) {
  const [search, setSearch] = useState("");
  // Confirm before delete
  const handleDelete = (e, id, title) => {
    e.stopPropagation();
    if (window.confirm(`Delete note: "${title}"? This cannot be undone.`)) {
      onDelete(id);
    }
  };
  // Filter notes by search
  const filteredNotes = notes.filter(note =>
    note.title.toLowerCase().includes(search.toLowerCase())
  );
  return (
    <div className="relative w-1/4 min-w-[260px] bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900 border-r border-gray-800 p-4 flex flex-col h-full overflow-y-auto">
      {/* Blurred animated background */}
      <div className="absolute top-0 left-0 w-60 h-60 bg-blue-700 opacity-20 rounded-full filter blur-2xl animate-pulse -z-10" style={{animationDuration: '7s'}}></div>
      <div className="absolute bottom-0 right-0 w-60 h-60 bg-purple-700 opacity-20 rounded-full filter blur-2xl animate-pulse -z-10" style={{animationDuration: '9s'}}></div>
      <h2 className="font-bold text-white text-lg mb-4 tracking-wide">Notes</h2>
      <input
        className="mb-3 px-3 py-2 rounded bg-gray-800 text-gray-100 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        placeholder="Search notes..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        aria-label="Search notes"
      />
      <ul className="space-y-2" tabIndex={0} aria-label="Notes">
        {filteredNotes.length === 0 && (
          <li className="text-gray-400 text-sm text-center py-8">No notes found</li>
        )}
        {filteredNotes.map(note => (
          <li
            key={note._id}
            className={`flex justify-between items-center p-3 rounded-lg cursor-pointer transition-all border-2 shadow-sm group ${
              selectedId === note._id
                ? "bg-blue-900/60 border-blue-500 ring-2 ring-blue-400"
                : "bg-gray-800/60 border-transparent hover:bg-gray-700/80 hover:border-blue-400"
            }`}
            onClick={() => onSelect(note)}
            tabIndex={0}
            aria-selected={selectedId === note._id}
            onKeyDown={e => {
              if (e.key === "Enter" || e.key === " ") onSelect(note);
            }}
          >
            <span className="truncate text-white font-medium" title={note.title}>{note.title}</span>
            <div className="flex items-center gap-1">
              {/* Pin button for future feature */}
              <button
                className="text-yellow-400 opacity-0 group-hover:opacity-80 transition-opacity p-1 rounded focus:outline-none focus:ring-2 focus:ring-yellow-400"
                title="Pin note (coming soon)"
                tabIndex={-1}
                disabled
              >
                <svg width="18" height="18" fill="none" viewBox="0 0 24 24"><path fill="currentColor" d="M7 2v2H5a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h1v7.382l-1.447 1.724A1 1 0 0 0 5.618 20h12.764a1 1 0 0 0 .765-1.894L18 15.382V8h1a1 1 0 0 0 1-1V5a1 1 0 0 0-1-1h-2V2H7zm2 2V4h6v2H9V4zm-2 2h10v2H7V6zm2 4h6v7.382l1.447 1.724H7.553L9 17.382V10z"/></svg>
              </button>
              <button
                className="text-red-400 ml-2 focus:outline-none focus:ring-2 focus:ring-red-400 rounded hover:bg-red-900/30 p-1"
                aria-label={`Delete note ${note.title}`}
                onClick={e => handleDelete(e, note._id, note.title)}
                tabIndex={-1}
              >
                🗑️
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
