import React, { useEffect, useState } from "react";
import axios from "axios";
import LatexEditor from "./components/LatexEditor";
import NotesList from "./components/NotesList";

const API = "http://localhost:5000/api/notes";

export default function App() {
  const [notes, setNotes] = useState([]);
  const [selected, setSelected] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  // Fetch notes
  useEffect(() => {
    axios.get(API).then(res => setNotes(res.data));
  }, []);

  // Select note
  const handleSelect = note => {
    setSelected(note);
    setTitle(note.title);
    setContent(note.content);
  };

  // Save note (create or update)
  const handleSave = async () => {
    if (selected) {
      // Update
      const res = await axios.put(`${API}/${selected._id}`, {
        title,
        content
      });
      setNotes(notes =>
        notes.map(n => (n._id === res.data._id ? res.data : n))
      );
      setSelected(res.data);
    } else {
      // Create
      const res = await axios.post(API, { title, content });
      setNotes([res.data, ...notes]);
      setSelected(res.data);
    }
  };

  // Delete note
  const handleDelete = async id => {
    await axios.delete(`${API}/${id}`);
    setNotes(notes => notes.filter(n => n._id !== id));
    if (selected && selected._id === id) {
      setSelected(null);
      setTitle("");
      setContent("");
    }
  };

  // New note
  const handleNew = () => {
    setSelected(null);
    setTitle("");
    setContent("");
  };

  return (
    <div className="flex h-screen">
      <NotesList
        notes={notes}
        onSelect={handleSelect}
        onDelete={handleDelete}
        selectedId={selected?._id}
      />
      <div className="flex-1 p-4">
        <div className="flex items-center gap-2 mb-4">
          <input
            className="border p-2 flex-1"
            placeholder="Title"
            value={title}
            onChange={e => setTitle(e.target.value)}
          />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={handleSave}
            disabled={!title || !content}
          >
            {selected ? "Update" : "Create"}
          </button>
          <button
            className="bg-gray-300 px-4 py-2 rounded"
            onClick={handleNew}
          >
            New
          </button>
        </div>
        <LatexEditor value={content} setValue={setContent} />
      </div>
    </div>
  );
}
