import React, { useState } from "react";
import { Pin, PinOff, Trash2 } from "lucide-react";

export default function NotesList({ notes, onSelect, onDelete, onPin, selectedId }) {
  const [search, setSearch] = useState("");
  // Confirm before delete
  const handleDelete = (e, id, title) => {
    e.stopPropagation();
    if (window.confirm(`Delete note: "${title}"? This cannot be undone.`)) {
      onDelete(id);
    }
  };
  // Pin/unpin note
  const handlePin = (e, id, pinned) => {
    e.stopPropagation();
    onPin(id, !pinned);
  };
  // Filter notes by search
  const filteredNotes = notes.filter(note =>
    note.title.toLowerCase().includes(search.toLowerCase())
  );
  const pinnedNotes = filteredNotes.filter(n => n.pinned);
  const otherNotes = filteredNotes.filter(n => !n.pinned);
  return (
    <div className="relative w-1/4 min-w-[260px] bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900 border-r border-gray-800 p-4 flex flex-col h-full overflow-y-auto">
      {/* Blurred animated background */}
      <div className="absolute top-0 left-0 w-60 h-60 bg-blue-700 opacity-20 rounded-full filter blur-2xl animate-pulse -z-10" style={{animationDuration: '7s'}}></div>
      <div className="absolute bottom-0 right-0 w-60 h-60 bg-purple-700 opacity-20 rounded-full filter blur-2xl animate-pulse -z-10" style={{animationDuration: '9s'}}></div>
      <h2 className="font-bold text-white text-lg mb-4 tracking-wide">Notes</h2>
      <input
        className="mb-3 px-3 py-2 rounded bg-gray-800 text-gray-100 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        placeholder="Search notes..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        aria-label="Search notes"
      />
      {/* Important section */}
      {pinnedNotes.length > 0 && (
        <>
          <div className="text-yellow-300 font-semibold mb-1 mt-2">Important</div>
          <ul className="space-y-2 mb-4" tabIndex={0} aria-label="Important notes">
            {pinnedNotes.map((note, idx) => (
              <li
                key={note._id}
                className={`relative flex justify-between items-center p-3 rounded-lg cursor-pointer transition-all border-2 shadow-sm group 
                  ${selectedId === note._id
                    ? "bg-blue-900/60 border-blue-500 ring-2 ring-blue-400"
                    : idx % 2 === 0
                      ? "bg-yellow-900/60 border-transparent hover:bg-blue-900/40 hover:border-blue-400"
                      : "bg-yellow-800/60 border-transparent hover:bg-blue-900/40 hover:border-blue-400"}
                `}
                onClick={() => onSelect(note)}
                tabIndex={0}
                aria-selected={selectedId === note._id}
                onKeyDown={e => {
                  if (e.key === "Enter" || e.key === " ") onSelect(note);
                }}
              >
                {/* Blurred background */}
                <div className="absolute inset-0 w-full h-full bg-yellow-400/10 blur-xl rounded-lg -z-10 pointer-events-none" />
                {/* Left dash */}
                <div className="absolute left-0 top-6 h-6 w-1 rounded bg-yellow-400/80" />
                <span className="truncate text-white font-medium ml-3" title={note.title}>{note.title}</span>
                <div className="flex items-center gap-1 ml-auto">
                  <button
                    className="text-yellow-400 opacity-100 transition-opacity p-1 rounded focus:outline-none focus:ring-2 focus:ring-yellow-400"
                    title="Unpin note"
                    tabIndex={-1}
                    onClick={e => handlePin(e, note._id, note.pinned)}
                  >
                    <Pin fill="currentColor" size={18} />
                  </button>
                  <button
                    className="text-red-400 ml-2 focus:outline-none focus:ring-2 focus:ring-red-400 rounded hover:bg-red-900/30 p-1"
                    aria-label={`Delete note ${note.title}`}
                    onClick={e => handleDelete(e, note._id, note.title)}
                    tabIndex={-1}
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </>
      )}
      {/* Other notes */}
      <ul className="space-y-2" tabIndex={0} aria-label="Notes">
        {otherNotes.length === 0 && filteredNotes.length === 0 && (
          <li className="text-gray-400 text-sm text-center py-8">No notes found</li>
        )}
        {otherNotes.map((note, idx) => (
          <li
            key={note._id}
            className={`relative flex justify-between items-center p-3 rounded-lg cursor-pointer transition-all border-2 shadow-sm group 
              ${selectedId === note._id
                ? "bg-blue-900/60 border-blue-500 ring-2 ring-blue-400"
                : idx % 2 === 0
                  ? "bg-gray-800/60 border-transparent hover:bg-blue-900/40 hover:border-blue-400"
                  : "bg-gray-700/60 border-transparent hover:bg-blue-900/40 hover:border-blue-400"}
            `}
            onClick={() => onSelect(note)}
            tabIndex={0}
            aria-selected={selectedId === note._id}
            onKeyDown={e => {
              if (e.key === "Enter" || e.key === " ") onSelect(note);
            }}
          >
            {/* Blurred background */}
            <div className="absolute inset-0 w-full h-full bg-blue-400/10 blur-xl rounded-lg -z-10 pointer-events-none" />
            {/* Left dash */}
            <div className="absolute left-0 top-6 h-6 w-1 rounded bg-blue-400/80" />
            <span className="truncate text-white font-medium ml-3" title={note.title}>{note.title}</span>
            <div className="flex items-center gap-1 ml-auto">
              <button
                className="text-yellow-400 opacity-0 group-hover:opacity-80 transition-opacity p-1 rounded focus:outline-none focus:ring-2 focus:ring-yellow-400"
                title="Pin note"
                tabIndex={-1}
                onClick={e => handlePin(e, note._id, note.pinned)}
              >
                {note.pinned ? <Pin fill="currentColor" size={18} /> : <PinOff size={18} />}
              </button>
              <button
                className="text-red-400 ml-2 focus:outline-none focus:ring-2 focus:ring-red-400 rounded hover:bg-red-900/30 p-1"
                aria-label={`Delete note ${note.title}`}
                onClick={e => handleDelete(e, note._id, note.title)}
                tabIndex={-1}
              >
                <Trash2 size={18} />
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
