import React from "react";

export default function NotesList({ notes, onSelect, onDelete, selectedId }) {
  // Confirm before delete
  const handleDelete = (e, id, title) => {
    e.stopPropagation();
    if (window.confirm(`Delete note: "${title}"? This cannot be undone.`)) {
      onDelete(id);
    }
  };

  return (
    <div className="w-1/4 border-r p-2" role="navigation" aria-label="Notes list">
      <h2 className="font-bold mb-2">Notes</h2>
      <ul className="space-y-1" tabIndex={0} aria-label="Notes">
        {notes.map(note => (
          <li
            key={note._id}
            className={`flex justify-between items-center p-2 rounded cursor-pointer transition-colors border ${
              selectedId === note._id
                ? "bg-blue-200 border-blue-500 ring-2 ring-blue-400"
                : "hover:bg-gray-100 border-transparent"
            }`}
            onClick={() => onSelect(note)}
            tabIndex={0}
            aria-selected={selectedId === note._id}
            onKeyDown={e => {
              if (e.key === "Enter" || e.key === " ") onSelect(note);
            }}
          >
            <span className="truncate" title={note.title}>{note.title}</span>
            <button
              className="text-red-500 ml-2 focus:outline-none focus:ring-2 focus:ring-red-400 rounded"
              aria-label={`Delete note ${note.title}`}
              onClick={e => handleDelete(e, note._id, note.title)}
            >
              üóëÔ∏è
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
